mapper.select:
SELECT 
	count(*) as totalCount ,
	sum(fc.length) as totalTime
FROM 
	t_fact_call fc
WHERE
	fc.call_type = #{call_type} AND 
	fc.user_id = #{user_id} 
	AND fc.start_time  >  #{start_time}
	AND fc.end_time  <  #{end_time}
	LIMIT #{pageStart} ,#{pageEnd}

SELECT 
	count(*)
FROM 
	t_fact_call fc
WHERE
	fc.call_type = #{call_type} AND 
	fc.user_id = #{user_id} 
	AND fc.start_time  >  #{start_time}
	AND fc.end_time  <  #{end_time}

SELECT 
	count(*)
FROM 
	t_fact_call fc
LEFT JOIN 
	t_tenant t ON fc.tenant_id = t.id
WHERE
	fc.call_type = #{call_type} 
	AND fc.start_time  >  #{start_time}
	AND fc.end_time  <  #{end_time}
